// src/components/Motorcycles/CategoryDetails.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getData } from '../../Api';
import MotorcycleCard from './MotorcycleCard';
import '../styles/CategoryDetails.css';

const CategoryDetails = () => {
    const { categoryName } = useParams();
    const [bikes, setBikes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        getData(`api/get-bike/category/`)
            .then(data => {
                setBikes(data);
                setLoading(false);
            })
            .catch(err => {
                console.error(err);
                setError(err.message);
                setLoading(false);
            });
    }, [categoryName]);

    if (loading) return <div className="category-loading">Loading bikes...</div>;
    if (error) return <div className="category-error">Error: {error}</div>;

    return (
        <div className="category-details-container">
            <h1>{categoryName.charAt(0).toUpperCase() + categoryName.slice(1)} Bikes</h1>
            <div className="bikes-grid">
                {bikes.map(bike => (
                    <MotorcycleCard
                        key={bike.id}
                        id={bike.id}
                        image={bike.image1}
                        name={bike.name}
                        year={bike.year}
                        category={bike.category}
                        displacement={bike.displacement}
                        description={bike.about || bike.description || 'No description.'}
                        price={bike.price}
                    />
                ))}
            </div>
            {bikes.length === 0 && <div className="no-bikes">No bikes found for this category.</div>}
        </div>
    );
};

export default CategoryDetails;
