import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import MotorcycleCard from './MotorcycleCard';
import { getData } from '../../Api';
import '../styles/BrandDetails.css';
import '../styles/MotorcycleCard.css'; // Ensure MotorcycleCard styles are imported

const BrandDetails = () => {
  const { brandId } = useParams();
  const [bikes, setBikes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [brandName, setBrandName] = useState('');

  useEffect(() => {
    const fetchBrandBikes = async () => {
      try {
        // Assuming your API has an endpoint like /api/bikes/brand/:brandId
        const data = await getData(`/api/bikes/brand/${brandId}`);
        setBikes(data);
        // Assuming the first bike in the list has the brand name, or you might need a separate endpoint
        if (data.length > 0) {
          setBrandName(data[0].brand);
        } else {
           // Handle case where no bikes are found for the brand
           setBrandName(brandId.replace(/-/g, ' ').replace(/\b\w/g, l => l.toUpperCase())); // Basic formatting if no data
        }
        setLoading(false);
      } catch (err) {
        setError(err);
        setLoading(false);
      }
    };

    fetchBrandBikes();
  }, [brandId]);

  if (loading) {
    return <div>Loading bikes...</div>; // Reverted back to text
  }

  if (error) {
    return <div className="error-message">Error loading bikes for {brandId}: {error.message}</div>;
  }

  return (
    <div className="brand-details-container">
      <h2>{brandName || 'Brand'} Motorcycles</h2>
      {bikes.length === 0 ? (
        <p>No motorcycles found for this brand.</p>
      ) : (
        <div className="brand-bikes-list">
          {bikes.map(bike => (
            <MotorcycleCard key={bike._id} bike={bike} />
          ))}
        </div>
      )}
    </div>
  );
};

export default BrandDetails;
