import { useState, useEffect } from 'react';
import { useNavigate, useParams, BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { getData } from '../../Api'; // Assuming getData fetches data from your API
import MotorcycleCard from './MotorcycleCard';
import '../styles/CategoryPage.css';
import '../styles/BikeList.css';
import '../styles/loading.css';

// Import category images
import AdventureImg from '../assets/Bike categories/Adventure.png';
import CruiserImg from '../assets/Bike categories/Cruiser.png';
import DirtImg from '../assets/Bike categories/Dirt.png';
import RetroImg from '../assets/Bike categories/Retro.png';
import SportImg from '../assets/Bike categories/sports.png';
import StreetImg from '../assets/Bike categories/street.png';
import SuperSportImg from '../assets/Bike categories/super.png';
import TourerImg from '../assets/Bike categories/Tourer.png';

const categories = [
    { name: 'Street', image: StreetImg },
    { name: 'Sport', image: SportImg },
    { name: 'Adventure', image: AdventureImg },
    { name: 'Cruiser', image: CruiserImg },
    { name: 'SuperSport', image: SuperSportImg },
    { name: 'Retro', image: RetroImg },
    { name: 'Tourer', image: TourerImg },
    { name: 'Dirt', image: DirtImg },
];

const CategoryHome = () => {
    const navigate = useNavigate();

    const handleCategoryClick = (category) => {
        navigate(`/category/${category}`);
    };

    return (
        <section className="category-section-homepage">
            <h2>Browse by Category</h2>
            <div className="categories-grid-homepage">
                {categories.map((category, index) => (
                    <div
                        key={index}
                        className="category-item-homepage"
                        onClick={() => handleCategoryClick(category.name)}
                    >
                        <img
                            src={category.image}
                            alt={category.name}
                            className="category-image-homepage"
                        />
                    </div>
                ))}
            </div>
        </section>
    );
};

const CategoryBikeList = () => {
    const { category } = useParams();
    const [bikes, setBikes] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        getData(`api/get-bike/category/${category}`)
            .then(data => {
                setBikes(data);
                setLoading(false);
            })
            .catch(err => {
                setError(err.message);
                setLoading(false);
            });
    }, [category]);

    if (loading) {
        return (
            <div className="loading-container">
                <div className="loading">
                    <span></span><span></span><span></span><span></span><span></span>
                </div>
                <p>Loading {category} Bikes</p>
            </div>
        );
    }

    if (error) return <p>Error: {error}</p>;

    if (bikes.length === 0) return <p>No bikes found in {category} category.</p>;

    return (
        <div className="bikes-container">
            <h2 className="section-title">{category} Bikes</h2>
            <div className="bike-list">
                {bikes.map(bike => (
                    <MotorcycleCard
                        key={bike.id}
                        id={bike.id}
                        image={bike.image1}
                        name={bike.name}
                        year={bike.year || '—'}
                        category={bike.category || '—'}
                        displacement={bike.displacement}
                        description={bike.about || 'No description.'}
                        price={bike.price}
                        isTrending={false}
                    />
                ))}
            </div>
        </div>
    );
};

const CategoryPage = () => {
    return (
        <Router>
            <Routes>
                <Route path="/" element={<CategoryHome />} />
                <Route path="/category/:category" element={<CategoryBikeList />} />
            </Routes>
        </Router>
    );
};

export default CategoryPage;
