// src/components/layout/CategoryDetails.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getData } from '../../Api';
import '../styles/CategoryDetails.css';
import '../styles/loading.css';
import MotorcycleCard from './MotorcycleCard';

const CategoryDetails = () => {
  const { categoryName } = useParams();
  const [bikes, setBikes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Optional mapping for display names if you want
  const categoryInfo = {
    street: 'Street',
    sport: 'Sport',
    adventure: 'Adventure',
    cruiser: 'Cruiser',
    supersport: 'SuperSport',
    retro: 'Retro',
    tourer: 'Tourer',
    dirt: 'Dirt',
  };

  useEffect(() => {
    setLoading(true);
    getData(`api/get-bike/category/${categoryName}`)
      .then(data => {
        setBikes(data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message);
        setLoading(false);
      });
  }, [categoryName]);

  if (!categoryInfo[categoryName.toLowerCase()]) {
    return <div className="category-not-found">Category not found</div>;
  }

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading">
          <span></span><span></span><span></span><span></span><span></span>
        </div>
        <p>Loading Bikes</p>
      </div>
    );
  }

  if (error) {
    return <div className="error">Error: {error}</div>;
  }

  return (
    <div className="category-details-container">
      <div className="category-header">
        <h1>{categoryInfo[categoryName.toLowerCase()]}</h1>
        <p className="category-description">
          Browse all bikes in the {categoryInfo[categoryName.toLowerCase()]} category.
        </p>
      </div>

      <div className="bikes-grid">
        {bikes.map(bike => (
          <MotorcycleCard
            key={bike.id}
            id={bike.id}
            image={bike.image1}
            name={bike.name}
            year={bike.year}
            category={bike.category}
            displacement={bike.displacement}
            description={bike.about || bike.description || 'No description.'}
            price={bike.price}
          />
        ))}
      </div>

      {bikes.length === 0 && (
        <div className="no-bikes">
          No bikes found for {categoryInfo[categoryName.toLowerCase()]}.
        </div>
      )}
    </div>
  );
};

export default CategoryDetails;
