// src/components/Motorcycles/BikeList.js
import React, { useState, useEffect } from 'react';
import MotorcycleCard from './MotorcycleCard';
import { getData } from '../../Api';
import '../styles/BikeList.css';
import '../styles/MotorcycleCard.css'; // Ensure MotorcycleCard styles are imported

const BikeList = ({ title, endpoint }) => {
  const [bikes, setBikes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchBikes = async () => {
      try {
        const data = await getData(endpoint);
        setBikes(data);
        setLoading(false);
      } catch (err) {
        setError(err);
        setLoading(false);
      }
    };

    fetchBikes();
  }, [endpoint]);

  if (loading) {
    return (
      <div className="loading"> {/* Replaced "Loading bikes..." with the loading animation HTML */}
        <span></span>
        <span></span>
        <span></span>
        <span></span>
        <span></span>
      </div>
    );
  }

  if (error) {
    return <div className="error-message">Error loading bikes: {error.message}</div>;
  }

  return (
    <section className="bike-list-section">
      <h2>{title}</h2>
      <div className="bike-cards-container">
        {bikes.map(bike => (
          <MotorcycleCard key={bike._id} bike={bike} />
        ))}
      </div>
    </section>
  );
};

export default BikeList;
