// src/components/Motorcycles/CategoryDetails.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { getData } from '../../Api';
import MotorcycleCard from './MotorcycleCard';
import '../styles/CategoryDetails.css';

const CategoryDetails = () => {
  const { categoryName } = useParams(); // This will now be case-sensitive
  const [bikes, setBikes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Use actual case-sensitive keys
  const categoryInfo = {
    Street: { displayName: 'Street' },
    Sport: { displayName: 'Sport' },
    Adventure: { displayName: 'Adventure' },
    Cruiser: { displayName: 'Cruiser' },
    SuperSport: { displayName: 'SuperSport' },
    Retro: { displayName: 'Retro' },
    Tourer: { displayName: 'Tourer' },
    Dirt: { displayName: 'Dirt' },
  };

  useEffect(() => {
    const fetchCategoryBikes = async () => {
      setLoading(true);
      setError(null);
      try {
        const data = await getData(`api/get-bike/category/${categoryName}`);
        setBikes(data);
      } catch (err) {
        setError(err.message || 'Failed to load bikes.');
      } finally {
        setLoading(false);
      }
    };

    if (categoryInfo[categoryName]) {
      fetchCategoryBikes();
    } else {
      setError('Category not found.');
      setLoading(false);
    }
  }, [categoryName]);

  if (error) {
    return <div className="error">Error: {error}</div>;
  }

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading">
          <span></span><span></span><span></span><span></span><span></span>
        </div>
        <p>Loading Bikes</p>
      </div>
    );
  }

  return (
    <div className="category-details-container">
      <div className="category-header">
        <h1>{categoryInfo[categoryName].displayName}</h1>
        <p className="category-description">
          Browse all bikes in the {categoryInfo[categoryName].displayName} category.
        </p>
      </div>

      <div className="bikes-grid">
        {bikes.map(bike => (
          <MotorcycleCard
            key={bike.id}
            id={bike.id}
            image={bike.image1}
            name={bike.name}
            year={bike.year}
            category={bike.category}
            displacement={bike.displacement}
            description={bike.about || bike.description || 'No description.'}
            price={bike.price}
          />
        ))}
      </div>

      {bikes.length === 0 && (
        <div className="no-bikes">
          No bikes found for {categoryInfo[categoryName].displayName}.
        </div>
      )}
    </div>
  );
};

export default CategoryDetails;
