// src/components/layout/CategoryDetails.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { getData } from '../../Api';
import '../styles/CategoryDetails.css';
import '../styles/loading.css'; // For loading animation styles
import MotorcycleCard from './MotorcycleCard';

const CategoryDetails = () => {
  const { categoryName } = useParams();
  const [bikes, setBikes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const categoryInfo = {
    Street: { displayName: 'Street' },
    Sport: { displayName: 'Sport' },
    Adventure: { displayName: 'Adventure' },
    Cruiser: { displayName: 'Cruiser' },
    Supersport: { displayName: 'SuperSport' },
    Retro: { displayName: 'Retro' },
    Tourer: { displayName: 'Tourer' },
    dirt: { displayName: 'Dirt' },
  };

  useEffect(() => {
    const categoryKey = categoryName;
    if (!categoryInfo[categoryKey]) {
      setError('Invalid category');
      setLoading(false);
      return;
    }

    setLoading(true);
    getData(`api/get-bike/category/${categoryKey}`)
      .then(data => {
        setBikes(data);
        setLoading(false);
      })
      .catch(err => {
        setError(err.message || 'Failed to load bikes');
        setLoading(false);
      });
  }, [categoryName]);

  if (error) {
    return <div className="error">Error: {error}</div>;
  }

  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading">
          <span></span><span></span><span></span><span></span><span></span>
        </div>
        <p>Loading Bikes</p>
      </div>
    );
  }

  const displayName = categoryInfo[categoryName.toLowerCase()].displayName;

  return (
    <div className="category-details-container">
      <div className="category-header">
        <h1>{displayName}</h1>
        <p className="category-description">
          Browse all bikes in the {displayName} category.
        </p>
      </div>

      <div className="bikes-grid">
        {bikes.map(bike => (
          <MotorcycleCard
            key={bike.id}
            id={bike.id}
            image={bike.image1}
            name={bike.name}
            year={bike.year}
            category={bike.category}
            displacement={bike.displacement}
            description={bike.about || bike.description || 'No description.'}
            price={bike.price}
          />
        ))}
      </div>

      {bikes.length === 0 && (
        <div className="no-bikes">No bikes found for {displayName}.</div>
      )}
    </div>
  );
};

export default CategoryDetails;
